import React from 'react';
import {Route} from 'react-router-dom'
import {connect} from 'react-redux'
import {createStructuredSelector} from 'reselect'

import {fetchCollectionsStartAsync} from '../../redux/shop/shop.actions'
import { selectIsFetching, selectIsCollectionsLoaded } from '../../redux/shop/shop.selectors'

import WithSpinner from '../../components/with-spinner/with-spinner.component'
import CollectionOverviewContainer from '../../components/collection-overview/collection-overview.container'
import CollectionPage from '../collection/collection.component'

const CollectionPageWithSpinner = WithSpinner(CollectionPage)

class ShopPage  extends React.Component {

  componentDidMount() {
    this.props.fetchCollectionsStartAsync()
  }
  render() {
    const { match, isCollectionLoaded} = this.props
    return (
      <div className='shop-page'>
        <Route exact path={`${match.path}`} component={CollectionOverviewContainer}></Route>
        <Route path={`${match.path}/:collectionId`} render={(props) => <CollectionPageWithSpinner isLoading={!isCollectionLoaded } {...props}/>}></Route>

      </div> 
    );
  }
}

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
})
const mapStateToProps = createStructuredSelector({
  isCollectionLoaded: selectIsCollectionsLoaded
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
